def.body:
    def after

{% if @type.methods.find do |d|
  d.name == "after"
end %}
        if previous_def
          {{ (yield).id }}
        end
      {% else %}{% if @type.superclass && @type.superclass.methods.find do |d|
  d.name == "after"
end %}
        if super
          {{ (yield).id }}
        end
      {% else %}
        {{ (yield).id }}
      {% end %}{% end %}


end

    def around(&block)

{% if existing = @type.methods.find do |d|
  d.name == "around"
end %}
        {% if @type.superclass && @type.superclass.methods.any? do |d|
  d.name == "around"
end %}
          {% body = "\n" %}
          {% for exp in (yield).expressions %}
            {% if "#{exp}".includes?("yield") %}
              {% body = (body + ("#{exp}".gsub(/yield/, "super{ yield }"))) + "\n" %}
            {% else %}
              {% body = (body + "#{exp}") + "\n" %}
            {% end %}
          {% end %}
          {% body = body + "\n" %}
          {{ (existing.body.stringify.gsub(/super\(\) do\n  yield\nend/, body)).id }}
        {% else %}
          previous_def do
            {{ (yield).id }}
          end
        {% end %}
      {% else %}
        {% if @type.superclass && @type.superclass.methods.any? do |d|
  d.name == "around"
end %}
          {% for exp in (yield).expressions %}
            {% if "#{exp}".includes?("yield") %}
              {{ ("#{exp}".gsub(/yield/, "super{ yield }")).id }}
            {% else %}
              {{ exp.id }}
            {% end %}
          {% end %}
        {% else %}
          {{ (yield).id }}
        {% end %}
      {% end %}


end

    def before

{% if existing = @type.methods.find do |d|
  d.name == "before"
end %}
        {% if @type.superclass && @type.superclass.methods.any? do |d|
  d.name == "before"
end %}
          {% to_insert = "#{yield} && super" %}
          {{ (existing.body.stringify.gsub(/super/, to_insert)).id }}
        {% else %}
          previous_def && {{ (yield).id }}
        {% end %}
      {% else %}
        {% if @type.superclass && @type.superclass.methods.any? do |d|
  d.name == "before"
end %}
          ({{ (yield).id }}) && super
        {% else %}
          {{ (yield).id }}
        {% end %}
      {% end %}


end
